<?phpclass Peminjaman_model extends CI_Model {	function get($query=FALSE,$id=FALSE) {	if ($query=='all') {			return $this->db->get('artikel')->result_array();	}	elseif ($query=='by_id') {			$this->db->where('id_artikel',$id);			return $this->db->get('artikel')->row();	}	}		function add($data) {		return $this->db->insert('artikel',$data);	}		function delete() {		return $this->db->delete('artikel');	}		function update($id,$data) {	$this->db->where('id_data',$id);	return $this->db->update('artikel',$data);	}}?>